import React from 'react';
import { Container, Form, Button, Row, Col } from 'react-bootstrap';
export default class login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {  
            usuario:'',
            pass:'',
        }
        /* comentario */
    }
    render() { 
        return ( 
          <Container id="login-container" style={{ marginTop: 200}}> 
              <Row>
               <Col>
                <Row>
                    <h2> Iniciar sesion</h2>
                    </Row>
                    
                        <Row>
                        <Col
                        sm="12"
                        xs="12"
                        md={{ span:4, offset:4 }}
                        lg={{ span:4, offset:4 }}
                        xl={{ span:4, offset:4 }}
                        >
                        

              <Form>
  <Form.Group >
    <Form.Label style={{ float: 'left'}}>Usuario</Form.Label>
    <Form.Control 
    onChange={(e) =>
        this.setState({ usuario: e.target.value})
    }
    />
    {
         /* this.state.usuario *
    }
   </Form.Group>

  <Form.Group >
    <Form.Label style={{ float: 'left'}}>Contraseña</Form.Label>
    <Form.Control 
    type="password"  
    onChange={(e) =>
        this.setState({ pass: e.target.value})
    }
    />
    
    {/* {this.state.pass} */}
  </Form.Group>
  <Button 
  variant="primary" 
   style={{ 
      marginTop: 20,
      width: '100%',
      }}>
      Iniciar sesión
  </Button>

  </Form>
  </Col>
  </Row>
  </Col>
  </Row>
</Container>

         );
    }
}
