import React from 'react';
import { Container, Row, Form, Button } from 'react-bootstrap';
import { request } from '../../helper/helper';
import Loading from '../../Loading/Loading';
import '../empleados.css'
import MessagePrompt from '../../prompts/message';
import ConfirmationPrompts from '../../prompts/confirmation';

export default class EmpleadosEditar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            idEmpleado : this.props.getIdEmpleado(),
            rediret: false,
            messsage: {
                text: '',
                show: false,
            },
            confirmation:{
                title
            },
            loading: false,
            empleados: {
                nombre: "",
                apellido_p: "",
                apellido_m: "",
                telefono: "",
                mail: "",
                direccion: "",
            },
          };
          this.onExitedMessage = this.onExitedMessage.bind(this);
    }
   
    componentDidMount(){
        this.getEmpleado();
    }
    getEmpleado(){
        this.setState({ loading: true });
        request
        .get(`/empleados/${this.state.idEmpleado}`)
        .then((response) => {
           this.setState({
               empleado: response.data,
               loading: false,
           });
        })
        .catch((err) => {
            console.error(err);
            this.setState({ loading: false });
        });
    }

    setValue(index, value){
        this.setState({
            empleados: {
                ...this.state.empleados,
                [index] : value,
            },
        });
    }

  
    guardarEmpleados() {
        this.setState({ loading: true });
        request
        .put(`/empleados/${this.state.idEmpleado}`, this.state.empleado)
        .then((Response) => {
            if (Response.data.exito){
                this.props.changeTab('buscar');
            }
            this.setState({ loading: false });
            
        })
        .catch((err) => {
            console.error(err);
            this.setState({ loading: true });
        });
    } 

    onExitedMessage(){
        if (this.state.rediret) this.props.changeTab( 'Buscar' );
    }

    render() { 
        return ( 
            <Container id="empleados-crear-container">

                <MessagePrompt 
                text={this.state.messsage.text}
                show={this.state.messsage.show} 
                duration={2500} //muestra unos sgundos en pantalla depsues de crear
                onExited={this.onExitedMessage}
                />

                <ConfirmationPrompts
                show={true}
                title=""
                text=""
                onCancel={() => console.log('cancelar')}
                onConfirm={() => console.log('confirmar')}

                />

                <Loading show={this.state.loading} />
                <Row>
                    <h2> EDITAR Empleados </h2>
                </Row>
                <Row>
                <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label id="empleado-label">Nombre</Form.Label>
                    <Form.Control 
                     value={this.state.empleados.nombre}
                      onChange={(e) => this.setValue('nombre' , e.target.value)}
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label id="empleado-label">Primer apellido</Form.Label>
                    <Form.Control 
                      value={this.state.empleados.apellido_p}
                      onChange={(e) => this.setValue('apellido_p' , e.target.value)}
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label id="empleado-label">Segundo apellido</Form.Label>
                    <Form.Control 
                      value={this.state.empleados.apellido_m}
                      onChange={(e) => this.setValue('apellido_m' , e.target.value)}
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label id="empleado-label">Telefono</Form.Label>
                    <Form.Control 
                      value={this.state.empleados.telefono}
                      onChange={(e) => this.setValue('telefono' , e.target.value)}
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label id="empleado-label">Correo electronico</Form.Label>
                    <Form.Control 
                      value={this.state.empleados.mail}
                      onChange={(e) => this.setValue('mail' , e.target.value)}
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label id="empleado-label">Direccion</Form.Label>
                    <Form.Control 
                      value={this.state.empleados.direccion}
                      onChange={(e) => this.setValue('direccion' , e.target.value)}
                    />
                </Form.Group>
               
                <Button variant="primary"
                 onClick={()=> console.log(this.guardarEmpleados())} 
                >
                    Guardar Empleado
                </Button>
                </Form>
                </Row>
            </Container>
         );
    }
}
