import React from 'react';
import { Modal } from 'react-bootstrap';
 
export default class MessagePrompt extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    render() { 
        return ( 
            <Modal id="message-prompt" centered show={true}>
            <Modal.Body>
              {
                this.props.text 
              }
            </Modal.Body>
          </Modal>
         );
    }
}
