import React from 'react';
import { Container, Row, Nav } from 'react-bootstrap';
import EmpleadosBuscar from './crud/buscar';
import EmpleadosCrear from './crud/crear';
import './empleados.css';


export default class Empleados extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            currentTab: 'buscar',
         };
    this.changeTab = this.changeTab.bind(this)
    }

    changeTab(tab){
        this.setState({current: tab});
    }
    render() { 
        return ( 
            <Container id="empleados-container">
                <Row>
                <Nav fill variant="tabs" 
                defaultActiveKey="/buscar"
                onSelect={(eventKey) => 
                   this.setState({ currentTab: eventKey})}
                >
                <Nav.Item>
                    <Nav.Link eventKey="Buscar">Buscar</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Crear">Crear</Nav.Link>
                </Nav.Item>
                  </Nav>
                </Row>
                <Row>
                    {this.state.currentTab === 'buscar' ? (
                    <EmpleadosBuscar 
                    chageTab={this.changeTab}
                    setId
                    /> 
                    ) : (
                      <EmpleadosCrear changeTab={(tab) => this.changeTab(tab)} />
                    ) }
                </Row>

            </Container>
         );
    }
}
